import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import { Button, CircularProgress } from "@material-ui/core";
import useForm from "../../CustomHooks/useForm";
import { useHistory } from "react-router-dom";

import { useStyles } from "./style";

export const SignUpForm = () => {
  const classes = useStyles();
  //esperar API ficar pronta para nomear os inputs
  const [form, handleInput] = useForm({
    name: "",
    email: "",
    phone: "",
    about: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const history = useHistory();

  const submitForm = (e) => {
    e.preventDefault();
  };

  return (
    <section>
      <form className={classes.form}>
        <div>
          <TextField
            required
            autoFocus
            value={form.escolaridade}
            //   onChange={handleInput}
            name="escolaridade"
            label="Seu grau de escolaridade:"
            variant="outlined"
            type="text"
            fullWidth
            className={classes.textField}
          />
        </div>
        <div>
          <TextField
            required
            value={form.instituicao}
            //   onChange={handleInput}
            name="instituicao"
            label="instituicao"
            placeholder="Sua instituição de ensino:"
            variant="outlined"
            type="email"
            fullWidth
            className={classes.textField}
          />
        </div>
        <div>
          <TextField
            required
            value={form.curso}
            //   onChange={handleInput}
            name="curso"
            label="Possui algum curso profissionalizante? Se sim, qual?"
            variant="outlined"
            fullWidth
            className={classes.textField}
          />
        </div>
        <div>
          <TextField
            required
            value={form.about}
            //   onChange={handleInput}
            name="street"
            label="Fale um pouco sobre você? O que você está buscando para sua próxima experiência profissional?"
            variant="outlined"
            type="text"
            fullWidth
            className={classes.textField}
          />
        </div>
        <div>
          <TextField
            required
            value={form.area}
            //   onChange={handleInput}
            name="area"
            label="Tem vontade de se profissionalizar em alguma área? Qual?"
            variant="outlined"
            type="text"
            fullWidth
            className={classes.textField}
          />
        </div>
        <div className={classes.divSubmit}>
          <Button type="submit" variant="contained" color="primary">
            {isLoading ? (
              <CircularProgress size={24} color={"inherit"} />
            ) : (
              "Pronto!"
            )}
          </Button>
        </div>
      </form>
    </section>
  );
};
