import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles((theme) => ({
  gridApresentacao: {
    background: "#fac710",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "70px",
    color: "#FFF",
  },
  root: {
    height: "100vh",
  },
  button: {
    width: "130px",
    height: "130px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "10px",
  },
  containerButtons: {
    width: "100%",
    height: "100vh",
  },
}));
