    return !!states.candidate.email;
  };
  const isCompany = () => {
    return !!states.company.email;
  };
  const isMentor = () => {
    return !!states.mentor.email;
  };
  return (
    <Paper className={classes.paper}>
      <Typography>
        Oi, Luiza Ferreira! :) Bem-vinda à sua rede de apoio para o mercado de
        trabalho! Estamos felizes em ver você por aqui. Esperamos que você tenha
        uma experiência incrível nessa jornada!
      </Typography>
    </Paper>
  );
}
