import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles((theme) => ({
  gridApresentacao: {
    background: theme.palette.primary.main,
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "70px",
    color: "#FFF",
    [theme.breakpoints.down("xs")]: {
      display: "none",
    },
  },
  root: {
    height: "100vh",
  },
  button: {
    margin: "25px 0",
    padding: "15px 0",
    borderRadius: "20px",
    boxShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
    fontFamily: "Lato",
    fontStyle: "normal",
    fontWeight: 900,
    fontSize: "36px",
    lineHeight: "43px",
    color: theme.palette.primary.main,
  },
  containerButtons: {
    padding: "30px",
    display: "flex",
    justifyContent: "space-between",
    flexDirection: "column",
  },
  titulo: {
    fontWeight: "bold",
  },

  subtitulo: {
    marginTop: "50px",
  },
  opcao: {
    marginBottom: "50px",
    color: "#696969",
    fontSize: "48px",
  },
  step: {
    color: "#494848",
    fontSize: "28px",
  },
  colorchip: {
    color: "#FFF",
    borderRadius: "100%",
    background: theme.palette.primary.main,
    padding: "10px",
    margin: "10px",
    fontSize: "18px",
  },

  textField: {
    marginBottom: "10px",
    boxShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
    borderRadius: "35px",
  },
}));
