import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles((theme) => ({
  gridApresentacao: {
    background: "#FFB800",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "70px",
    color: "#FFF",
  },
  root: {
    height: "100vh",
  },
  button: {
    margin: "10px 0",
    padding: "15px 0",
    borderRadius: "20px",
  },
  containerButtons: {
    padding: "30px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column",
  },
}));
